import { Link, Route, useHistory } from "react-router-dom";
import Home from "./Home";
import ReviewDay from "./ReviewDay";
import {useState} from "react";


function App() {
  const [day, setDay] = useState([
    "월",
    "화",
    "수",
    "목",
    "금",
    "토",
    "일"
  ]);

  const what_day = React.useRef(day); 

  let history = useHistory();
  return (
    <div className="App">
      <Link to="/">Home으로 가기</Link>
      <div onClick={()=>{
        history.goBack();
      }}>뒤로가기</div>
      <div style={{
        margin: "40px auto",
        backgroundColor: "lavender",
        minWidth:"8vh",
        maxWidth: "30vh",
        height: "80vh",
        flexDirection: "column",
        padding: "40px",
        borderRadius: "20px"
      }}>
        <Route path="/" exact>
          <Home day={day} ref={what_day}/>
        </Route>
        <Route path="/reviewday/:day" exact component={ReviewDay}>
          <ReviewDay day={day}/>
        </Route>
      </div>




      <Route path="/reviewday/:day" exact component={ReviewDay}>
        {/* <ReviewDay/> */}
      </Route>
    </div>
  );
}

export default App;
