import styled from "styled-components";
import { useParams } from "react-router-dom";

const ReviewDay = ({ day }) => {

    let what_day = React.useRef(day)
    return (
        <div style={{ textAlign: "center", wordBreak: "keep-all" }}>
            {day.map((day, i) => {
                return (
                    <div key={i}>

                        <h1 ref={what_day}> {day}요일 평점 남기기 </h1>
                        <div style={{
                            display: "flex",
                            justifyContent: "center",
                            alignItems: "center",
                            width: "100%",
                            margin: "1rem 0px"
                        }}>
                            <div style={{
                                width: "30px",
                                height: "30px",
                                borderRadius: "30px",
                                backgroundColor: "#8977ad",
                                margin: "5px"
                            }} />                <div style={{
                                width: "30px",
                                height: "30px",
                                borderRadius: "30px",
                                backgroundColor: "#8977ad",
                                margin: "5px"
                            }} />                <div style={{
                                width: "30px",
                                height: "30px",
                                borderRadius: "30px",
                                backgroundColor: "#8977ad",
                                margin: "5px"
                            }} />                <div style={{
                                width: "30px",
                                height: "30px",
                                borderRadius: "30px",
                                backgroundColor: "#8977ad",
                                margin: "5px"
                            }} />                <div style={{
                                width: "30px",
                                height: "30px",
                                borderRadius: "30px",
                                backgroundColor: "#8977ad",
                                margin: "5px"
                            }} /></div>


                        <button style={{
                            margin: "0.1em 0em",
                            backgroundColor: "#8977ad",
                            borderRadius: "30px",
                            padding: "14px",
                            width: "90%",
                            border: "1px",
                            color: "white",
                            fontWeight: "bold",
                            fontSize: "1.1em"
                        }}>평점 남기기</button>
                    </div>
                )
            }
            )
            }
        </div>
    )






}

export default ReviewDay;
