import React, { useReducer, useRef } from "react";
import { useParams, useHistory } from "react-router-dom";
import styled from "styled-components";
import ReviewDay from "./ReviewDay";
import ReviewStar from "./Star";

const Home = ({ day , targetDay}) => {
    let history = useHistory();
    let what_day = React.useRef(day)
    let what_star = React.useRef()
    return (

        <div style={{ textAlign: "center", wordBreak: "keep-all" }}>
            <h1> 내 일주일은? </h1>
            {day.map((i) => {
                return (
                    <div key={i} ref={targetDay} style={{
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                        width: "100%"}}>
                        <span>{i}</span>
                        <ReviewStar isRandom={true}></ReviewStar>


                            <Btn onClick={() => {
                                history.push("/reviewday");
                                targetDay.current = i;
                            }}
                            />

                    </div>
                );
            })}






            <div style={{
                fontWeight: "bold",
                fontSize: "1.5em",
                margin: "20px 0px"
            }}>
                평균 평점 <br />
                0.0
            </div>

            <button style={{
                margin: "0.1em 0em",
                backgroundColor: "#8977ad",
                borderRadius: "30px",
                padding: "14px",
                width: "100px",
                border: "1px",
                color: "white",
                fontWeight: "bold",
                fontSize: "1.1em"
            }}>Reset</button>

        </div>

    )

}


const Btn = styled.div`
margin-left: 10px;
width: 0;
height: 0;
border-bottom: 18px solid transparent;
border-top: 18px solid transparent;
border-left: 28px solid skyblue;
 border-right: 28px solid transparent
`;

export default Home;
