import Star from "./Star";

const Home = ({ day }) => {
    let history = useHistory();
    let what_day = React.useRef(day)
    let what_star = React.useRef()
    return (

        <div style={{textAlign: "center", wordBreak: "keep-all"}}>
            <h1> 내 일주일은? </h1>
            {day.map((day, i)=>{
        return (
          <div key={i}>
                 <div style={{
                display: "flex",
                justifyContent: "center",
