import React, { useReducer, useRef } from "react";
import { useParams, useHistory } from "react-router-dom";
import styled from "styled-components";
import ReviewDay from "./ReviewDay";

const Home = ({ day }) => {
    let history = useHistory();
    let what_day = React.useRef(day)
    return (

        <div style={{textAlign: "center", wordBreak: "keep-all"}}>



            <h1> 내 일주일은? </h1>
            <div style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                width: "100%"
            }}>
                <p style={{ margin: "0.5rem" }} ref={what_day} >{day[0]}</p>
                <div style={{
                    width: "30px",
                    height: "30px",
                    borderRadius: "30px",
                    backgroundColor: "#8977ad",
                    margin: "5px"
                }}/>
                <div style={{
                    width: "30px",
                    height: "30px",
                    borderRadius: "30px",
                    backgroundColor: "#8977ad",
                    margin: "5px"
                }}/>
                <div style={{
                    width: "30px",
                    height: "30px",
                    borderRadius: "30px",
                    backgroundColor: "#8977ad",
                    margin: "5px"
                }}/>
                <div style={{
                    width: "30px",
                    height: "30px",
                    borderRadius: "30px",
                    backgroundColor: "#8977ad",
                    margin: "5px"
                }}/>
                <div style={{
                    width: "30px",
                    height: "30px",
                    borderRadius: "30px",
                    backgroundColor: "#8977ad",
                    margin: "5px"
                }}/>
                <div onClick={() => {history.push("/reviewday/monday")}} style={{
                    marginLeft: "10px",
                      width: "0",
                      height: "0",
                      borderBottom: "18px solid transparent",
                      borderTop: "18px solid transparent",
                      borderLeft: "28px solid skyblue",
                      borderRight: "28px solid transparent"
                }}/>

            </div>




            <div>
                {day[1]}
            </div>
            <div>
                {day[2]}
            </div>
            <div>
                {day[3]}
            </div>
            <div>
                {day[4]}
            </div>
            <div>
                {day[5]}
            </div>
            <div>
                {day[6]}
            </div>
            <div>
                {day[7]}
            </div>

            <div style={{
                fontWeight: "bold",
                fontSize: "1.5em",
                margin: "20px 0px"
            }}>
                평균 평점 <br />
                0.0
            </div>

            <button style={{
                margin: "0.1em 0em",
                backgroundColor: "#8977ad",
                borderRadius: "30px",
                padding: "14px",
                width: "100px",
                border: "1px",
                color: "white",
                fontWeight: "bold",
                fontSize: "1.1em"
            }}>Reset</button>
        </div>
    )
}




export default Home;
