        // </div>
    )
}

const StarWrap = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Star = styled.span`
  display: inline-block;
  width: 40px;
  height: 40px;
  margin: 4px;
  background-color: #fff;
  border-radius: 100%;
  ${({ isRandom }) =>
    isRandom &&
    `width: 30px;
  height: 30px;`};
  &.active {
    background-color: orange;
  }
`;

export default ReviewStar;
