import Star from "./Star";


const ReviewDay = ({ targetDay }) => {
    let history = useHistory();
    targetDay = targetDay.current
    console.log(targetDay)
    // let what_day = day.map((day, i) => i)
    // console.log(what_day
    return (

        <div>
            <TargetDay> <span>{targetDay}요일</span> 평점 남기기 </TargetDay>
            <div style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                width: "100%",
                margin: "1rem 0px"
            }}>            <Star/> </div>


            <button onClick={() => {
                history.goBack();
            }} style={{
                margin: "0.1em 0em",
                backgroundColor: "#8977ad",
                borderRadius: "30px",
                padding: "14px",
                width: "90%",
                border: "1px",
                color: "white",
                fontWeight: "bold",
                fontSize: "1.1em"
            }}>평점 남기기</button>
        </div>

    )
}

const TargetDay = styled.h1`
text-align: center;
word-break: keep-all;
span {
    background-color: orange;
    border-radius: 20px;
}
`;

export default ReviewDay;
