import React, { useReducer } from "react";
import styled from "styled-components";
import { useParams, useHistory } from "react-router-dom";
import Star from "./Star";


const ReviewDay = ({ day }, index) => {
    let history = useHistory();
    // let what_day = day.map((day, i) => i)
    // console.log(what_day
    return (
<Provider>
            <div style={{ textAlign: "center", wordBreak: "keep-all" }}>
            <h1> {day}요일 평점 남기기 </h1>
            <div style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                width: "100%",
                margin: "1rem 0px"
            }}>            <Star/> </div>


            <button onClick={() => {
                history.goBack();
            }} style={{
                margin: "0.1em 0em",
                backgroundColor: "#8977ad",
                borderRadius: "30px",
                padding: "14px",
                width: "90%",
                border: "1px",
                color: "white",
                fontWeight: "bold",
                fontSize: "1.1em"
            }}>평점 남기기</button>
        </div>
</Provider>


    )
}

export default ReviewDay;
